import axios from "axios";

const LOCALHOST = "http://127.0.0.1:8000";
const LOCAL_NETWORK_IP = "http://192.168.1.100:8000"; // Replace with your local network IP

function getBaseUrl() {
    return window.location.hostname === "localhost"
        ? LOCALHOST
        : LOCAL_NETWORK_IP;
}

export async function getData(datapath) {
    const response = await axios.get(`${getBaseUrl()}${datapath}`);
    console.log(datapath);

    return response.data;
}

export async function getDataAndHeader(datapath, header) {
    const response = await axios.get(`${getBaseUrl()}${datapath}`, {
        headers: {
            authorization: `${header}`,
        },
    });
    console.log(response.data);
    return response.data;
}

export async function getDataDynamic(datapath, token = null) {
    const config = token
        ? {
              headers: {
                  "Content-Type": "application/json",
                  Authorization: `Bearer ${token}`,
              },
          }
        : {};
    const response = await axios.get(`${getBaseUrl()}${datapath}`, {}, config);
    return response.data;
}

export async function fetchUserDetails(token = null) {
    const config = token
        ? {
              headers: {
                  Authorization: `Bearer ${token}`,
              },
          }
        : {};
    const response = await axios.post(
        `${getBaseUrl()}/researcher/researcher/auto_login`,
        {},
        config
    );
    return response.data;
}

export async function postData(datapath, header, data) {
    const response = await axios.post(
        `${getBaseUrl()}${datapath}`,
        data,
        header
    );
    return response.data;
}

export async function postData2(datapath, data, config) {
    try {
        const response = await axios.post(
            `${getBaseUrl()}${datapath}`,
            data,
            config
        );
        return response.data;
    } catch (error) {
        throw error;
    }
}

export async function putData(datapath, header, data) {
    const response = await axios.put(`${getBaseUrl()}${datapath}`, data, {
        headers: {
            authorization: `${header}`,
        },
    });
    return response.data;
}

export async function patchData(datapath, header, data) {
    const response = await axios.patch(`${getBaseUrl()}${datapath}`, data, {
        headers: {
            authorization: `${header}`,
        },
    });
    return response.data;
}

export async function getImgData(datapath) {
    try {
        const response = await axios.get(`${getBaseUrl()}${datapath}`, {
            responseType: "blob",
        });
        return URL.createObjectURL(response.data);
    } catch (error) {
        console.error("Error fetching image data:", error);
        throw error;
    }
}
