import React from "react";
import logo from "../../resource/logo.png";

const newsItems = [
    {
        title: "Latest Research Breakthrough",
        body: "Our team has made a major scientific discovery that could revolutionize the field.",
        date: "November 10, 2023",
        related_laboratory: {
            title: "Quantum Computing Lab",
        },
    },
    {
        title: "New Laboratory Opening",
        body: "We are excited to announce the grand opening of our state-of-the-art nanotechnology facility.",
        date: "October 20, 2023",
        related_laboratory: {
            title: "Nanotechnology Research Center",
        },
    },
    {
        title: "Award-Winning Publication",
        body: "One of our researchers has been honored with a prestigious award for their groundbreaking work.",
        date: "September 5, 2023",
        related_laboratory: {
            title: "Material Science Institute",
        },
    },
    {
        title: "New AI Algorithm Developed",
        body: "Our AI team has developed a new algorithm that improves machine learning efficiency.",
        date: "August 15, 2023",
        related_laboratory: {
            title: "AI Research Lab",
        },
    },
    {
        title: "Breakthrough in Renewable Energy",
        body: "Researchers have discovered a new method to harness solar energy more efficiently.",
        date: "July 25, 2023",
        related_laboratory: {
            title: "Renewable Energy Lab",
        },
    },
    {
        title: "Innovative Cancer Treatment",
        body: "A new treatment developed by our team shows promise in fighting cancer.",
        date: "June 10, 2023",
        related_laboratory: {
            title: "Cancer Research Center",
        },
    },
];

const NewsCard = ({ title, body, date, related_laboratory }) => {
    const titleClass =
        title.length <= 20 ? "title-clamp short-title" : "title-clamp";
    return (
        <article className="flex flex-col rounded-3xl border border-gray-300 shadow-lg overflow-hidden min-w-[240px] w-[325px] bg-white">
            <div className="relative">
                <img
                    loading="lazy"
                    src={logo}
                    alt={title}
                    className="w-full h-48 object-cover"
                />
                <button className="absolute top-2 right-2 bg-blue-500 text-white px-4 py-2 rounded-full hover:bg-blue-600 transition">
                    Edit
                </button>
            </div>
            <div className="flex flex-col p-6 w-full bg-white">
                <div className="flex flex-col w-full text-gray-800">
                    <h3
                        className={`text-2xl font-bold leading-snug line-clamp-2 indent-clamp ${titleClass}`}
                    >
                        {title}
                        {title.length <= 20 ? <br /> : null}
                    </h3>
                    <p className="mt-2 text-base leading-6 line-clamp-3">
                        {body}
                    </p>
                </div>
                <div className="flex gap-4 items-center mt-6 w-full text-sm">
                    <img
                        loading="lazy"
                        src={logo}
                        alt={`${title} avatar`}
                        className="object-contain shrink-0 self-stretch my-auto w-12 h-12 rounded-full"
                    />
                    <div className="flex flex-col flex-1 shrink self-stretch my-auto basis-0 min-w-[240px]">
                        <div className="font-semibold text-gray-800">
                            {related_laboratory.title}
                        </div>
                        <div className="text-gray-600">{date}</div>
                    </div>
                </div>
            </div>
        </article>
    );
};

const RecentNews = ({ newsItems = [] }) => {
    return (
        <div className="flex flex-col px-16 py-28 w-full bg-gray-100 max-md:px-5 max-md:py-24 max-md:max-w-full">
            <div className="flex flex-wrap gap-10 justify-between items-end w-full max-md:max-w-full">
                <div className="flex flex-col text-gray-800 min-w-[240px] w-[768px] max-md:max-w-full">
                    <h2 className="text-5xl font-bold leading-tight max-md:max-w-full max-md:text-4xl">
                        Latest News
                    </h2>
                    <p className="mt-6 text-lg max-md:max-w-full">
                        Stay updated with our latest news and research
                        breakthroughs.
                    </p>
                </div>
                <button className="flex flex-col text-base text-gray-800 w-[104px]">
                    <span className="gap-2 self-stretch px-6 py-3 max-w-full bg-white rounded-xl border border-gray-300 hover:bg-gray-200 transition max-md:px-5">
                        View all
                    </span>
                </button>
            </div>
            <div className="flex flex-col mt-16 w-full max-md:mt-10 max-md:max-w-full">
                <div className="box-border flex relative flex-col shrink-0">
                    <div className="flex gap-8 items-start w-full max-md:max-w-full">
                        {newsItems.map((item, index) => (
                            <NewsCard
                                key={index}
                                title={item.title}
                                body={item.body}
                                date={item.date}
                                related_laboratory={item.related_laboratory}
                            />
                        ))}
                    </div>
                </div>
            </div>
        </div>
    );
};

const ProductSection = () => {
    return (
        <div className="flex flex-col px-16 py-28 w-full bg-white max-md:px-5 max-md:py-24 max-md:max-w-full">
            <div className="flex flex-col text-gray-800 min-w-[240px] w-[768px] max-md:max-w-full">
                <h2 className="text-5xl font-bold leading-tight max-md:max-w-full max-md:text-4xl">
                    Claude for Enterprise
                </h2>
                <p className="mt-6 text-lg max-md:max-w-full">
                    Product description goes here.
                </p>
            </div>
        </div>
    );
};

const AlignmentResearchSection = () => {
    return (
        <div className="flex flex-col px-16 py-28 w-full bg-white max-md:px-5 max-md:py-24 max-md:max-w-full">
            <div className="flex flex-col text-gray-800 min-w-[240px] w-[768px] max-md:max-w-full">
                <h2 className="text-5xl font-bold leading-tight max-md:max-w-full max-md:text-4xl">
                    Constitutional AI: Harmlessness from AI Feedback
                </h2>
                <p className="mt-6 text-lg max-md:max-w-full">
                    Alignment · Research
                </p>
                <p className="mt-6 text-lg max-md:max-w-full">15 ต.ค. 2565</p>
            </div>
        </div>
    );
};

const AnnouncementsSection = () => {
    return (
        <div className="flex flex-col px-16 py-28 w-full bg-white max-md:px-5 max-md:py-24 max-md:max-w-full">
            <div className="flex flex-col text-gray-800 min-w-[240px] w-[768px] max-md:max-w-full">
                <h2 className="text-5xl font-bold leading-tight max-md:max-w-full max-md:text-4xl">
                    Core Views on AI Safety: When, Why, What, and How
                </h2>
                <p className="mt-6 text-lg max-md:max-w-full">Announcements</p>
                <p className="mt-6 text-lg max-md:max-w-full">8 ม.ค. 2566</p>
            </div>
        </div>
    );
};

const CSSTailwind = () => {
    return (
        <>
            <ProductSection />
            <AlignmentResearchSection />
            <AnnouncementsSection />
            <RecentNews newsItems={newsItems} />
        </>
    );
};

export default CSSTailwind;
