import React from "react";

const newsItems = [
    {
        title: "Latest Research Breakthrough",
        body: "Our team has made a major scientific discovery that could revolutionize the field.",
        date: "November 10, 2023",
        related_laboratory: {
            title: "Quantum Computing Lab",
        },
    },
    {
        title: "New Laboratory Opening",
        body: "We are excited to announce the grand opening of our state-of-the-art nanotechnology facility.",
        date: "October 20, 2023",
        related_laboratory: {
            title: "Nanotechnology Research Center",
        },
    },
    {
        title: "Award-Winning Publication",
        body: "One of our researchers has been honored with a prestigious award for their groundbreaking work.",
        date: "September 5, 2023",
        related_laboratory: {
            title: "Material Science Institute",
        },
    },
];

const NewsCard = ({ title, body, date, ID, related_laboratory, type }) => {
    let relatedTopic;
    if (type === "News") {
        relatedTopic =
            related_laboratory.related_publication ||
            related_laboratory.related_research ||
            related_laboratory ||
            null;
    } else if (type === "Publication") {
        relatedTopic = null;
    } else if (type === "Laboratory") {
        relatedTopic = null;
    } else if (type === "Research") {
        relatedTopic = related_laboratory;
    } else {
        relatedTopic = "something wrong";
    }
    // console.log(relatedTopic);
    const { data, isLoading, isError } = useQueryGetImg(
        `http://127.0.0.1:8000/user`,
        type,
        ID
    );
    const [imgSmall, setImgSmall] = React.useState(kmitl_logo);

    React.useEffect(() => {
        const fetchImgSmall = async () => {
            console.log("before fetch img ", relatedTopic);

            if (relatedTopic) {
                if (relatedTopic.PID) {
                    const fetchedImg = await getImgData(
                        `http://127.0.0.1:8000/user/publication/image-low?publication_id=${relatedTopic.PID}`
                    );
                    setImgSmall(fetchedImg);
                } else if (relatedTopic.RID) {
                    console.log("ayo");
                    const fetchedImg = await getImgData(
                        `http://127.0.0.1:8000/user/research/image-low?research_id=${relatedTopic.RID}`
                    );
                    setImgSmall(fetchedImg);
                } else if (relatedTopic.LID) {
                    const fetchedImg = await getImgData(
                        `http://127.0.0.1:8000/user/laboratory/image-low?laboratory_id=${relatedTopic.LID}`
                    );
                    setImgSmall(fetchedImg);
                }
            }
        };
        fetchImgSmall();
    }, [relatedTopic]);
    const titleClass =
        title.length <= 20 ? "title-clamp short-title" : "title-clamp";
    return (
        <article className="flex flex-col rounded-3xl border border-black border-solid min-w-[240px] w-[325px]">
            <div className="relative">
                <img
                    loading="lazy"
                    src={isLoading ? kmitl_logo : data}
                    alt={title}
                    className="w-full rounded-tl-3xl rounded-tr-3xl aspect-[1.3]"
                />
                {/* Edit button overlay */}
                <button className="absolute top-2 right-2 bg-blue-500 text-white px-4 py-2 rounded-full hover:bg-blue-600 transition">
                    Edit
                </button>
            </div>
            <div className="flex flex-col p-6 w-full bg-cyan-200 rounded-bl-3xl rounded-br-3xl border border-black border-solid max-md:px-5">
                <div className="flex flex-col w-full text-black">
                    <h3
                        className={`text-2xl font-bold leading-snug line-clamp-2 indent-clamp ${titleClass}`}
                    >
                        {title}
                        {title.length <= 20 ? <br /> : null}
                    </h3>
                    <p className="mt-2 text-base leading-6 line-clamp-3">
                        {body}
                    </p>
                </div>
                {!(isLoading && isError) && relatedTopic !== null ? (
                    <div className="flex gap-4 items-center mt-6 w-full text-sm">
                        <img
                            loading="lazy"
                            src={imgSmall}
                            alt={`${title} avatar`}
                            className="object-contain shrink-0 self-stretch my-auto w-12 rounded-3xl aspect-square"
                        />
                        <div className="flex flex-col flex-1 shrink self-stretch my-auto basis-0 min-w-[240px]">
                            <div className="font-semibold text-black">
                                {relatedTopic?.title}
                            </div>
                            <div className="gap-2 self-stretch w-full text-black">
                                {date}
                            </div>
                        </div>
                    </div>
                ) : null}
            </div>
        </article>
    );
};

function RecentNews({ toFetchedData = {} }) {
    return (
        <section className="flex overflow-hidden flex-col px-16 py-28 w-full bg-sky-100 max-md:px-5 max-md:py-24 max-md:max-w-full">
            <div className="flex flex-wrap gap-10 justify-between items-end w-full max-md:max-w-full">
                <div className="flex flex-col text-black min-w-[240px] w-[768px] max-md:max-w-full">
                    <h2 className="text-5xl font-bold leading-tight max-md:max-w-full max-md:text-4xl">
                        Latest News
                    </h2>
                    {/* <img src={imgs} alt="a" /> */}
                    <p className="mt-6 text-lg max-md:max-w-full">
                        Lorem ipsum dolor sit amet, consectetur adipiscing elit.
                    </p>
                </div>
                <button className="flex flex-col text-base text-black w-[104px]">
                    <span className="gap-2 self-stretch px-6 py-3 max-w-full bg-white rounded-xl border border-black border-solid w-[104px] max-md:px-5">
                        View all
                    </span>
                </button>
            </div>
            <div className="flex flex-col mt-16 w-full max-md:mt-10 max-md:max-w-full">
                <div className="box-border flex relative flex-col shrink-0">
                    <div className="flex gap-8 items-start w-full max-md:max-w-full">
                        {!isLoading ? (
                            data.pages[0].map((item, index) => {
                                console.log("item[topic]", item[topic]);
                                console.log("topic", topic);
                                const topicData = item[topic];
                                const resolvedID =
                                    topic === "News"
                                        ? topicData.NID
                                        : topic === "Laboratory"
                                        ? topicData.LID
                                        : topic === "Research"
                                        ? topicData.RID
                                        : null;

                                return (
                                    <NewsCard
                                        key={`${index}`}
                                        {...item[topic]}
                                        type={`${topic}`}
                                        ID={resolvedID}
                                    />
                                );
                            })
                        ) : (
                            <div>Loading...</div>
                        )}
                    </div>
                </div>
                <div className="flex flex-wrap gap-10 justify-between items-center mt-12 w-full max-md:mt-10 max-md:max-w-full">
                    <div className="flex gap-2 items-start self-stretch my-auto"></div>
                    <div className="flex gap-4 items-start self-stretch my-auto">
                        <button className="flex gap-2 justify-center items-center px-3 w-12 h-12 bg-white border border-black border-solid rounded-[50px]">
                            <img
                                loading="lazy"
                                src={previous}
                                alt="Previous"
                                className="object-contain self-stretch my-auto w-6 aspect-square"
                            />
                        </button>
                        {!isError ? (
                            <button className="flex gap-2 justify-center items-center px-3 w-12 h-12 bg-white border border-black border-solid rounded-[50px]">
                                <img
                                    loading="lazy"
                                    src={next}
                                    alt="Next"
                                    className="object-contain self-stretch my-auto w-6 aspect-square"
                                />
                            </button>
                        ) : (
                            <button className="flex gap-2 justify-center items-center px-3 w-12 h-12 bg-white border border-black border-solid rounded-[50px]">
                                <img
                                    loading="lazy"
                                    src={next}
                                    alt="Next"
                                    className="object-contain self-stretch my-auto w-6 aspect-square"
                                    disabled
                                />
                            </button>
                        )}
                    </div>
                </div>
            </div>
        </section>
    );
}

const CSSTailwind = () => {
    return (
        <>
            <RecentNews rowData={newsItems} />
        </>
    );
};
export default CSSTailwind;
