              </button>
              <button
                onClick={() => setType2("Event")}
                className="py-4 bg-black text-white hover:bg-white hover:text-black transition-all ease-linear"
              >
                Event
              </button>
              <button
                onClick={() => setType2("Publication")}
                className="py-4 bg-black text-white hover:bg-white hover:text-black transition-all ease-linear"
              >
                Publication
              </button>
              <button
                onClick={() => setType2("Researcher")}
                className="py-4 bg-black text-white hover:bg-white hover:text-black transition-all ease-linear"
              >
                Researcher
              </button>
            </div>
          </React.Fragment>
        )}
        {!isCommit && isDynamic && type2 && (
          <RoleBasedActions
            role={highestRole}
            entity={type2}
            formMode={formMode}
          />
        )}
        {!isCommit && !isDynamic && (
          <RoleBasedActions
            role={highestRole}
            entity={type}
            formMode={formMode}
          />
        )}
        {formConfig && !isCommit && (
          <DynamicForm frame={formConfig} data={formData} />
        )}
        {isCommit ? (
          <>
            <div>
              <strong>Type: </strong>
              {type ? type : "error"}
            </div>
            <br />
            {console.log(fullData)}
            <DeepObjectViewer data={fullData} />
            <div className="flex gap-5 py-5">
              <button
                onClick={() => patchApproval(fullData)}
                className="bg-green-300 px-2 py-1 rounded-full border-2 border-transparent hover:border-green-700 transition-all ease-linear"
              >
                Verify!
              </button>
              <button
                className="bg-red-300 px-2 py-1 rounded-full border-2 border-transparent hover:border-red-700 transition-all ease-linear"
                onClick={() => {
                  setFormConfig(null);
                  setFormData(null);
                  dispatch(editAction.isSpecificClose());
                  dispatch(editAction.resetSpecificTypeAndIDAndData());
                  dispatch(editAction.resetIsCommit());
                  setIsDynamic(false);
                  setType3(null);
                }}
              >
                Reject..
              </button>
            </div>
          </>
        ) : null}

        <CloseButton
          onClick={() => {
            setFormConfig(null);
            setFormData(null);
            dispatch(editAction.isSpecificClose());
            dispatch(editAction.resetSpecificTypeAndIDAndData());
            dispatch(editAction.resetIsCommit());
            setIsDynamic(false);
            setType3(null);
          }}
        />
      </ModalFrame2>
    </>
  );
}

