import React from "react";

const newsItems = [
    {
        title: "Latest Research Breakthrough",
        body: "Our team has made a major scientific discovery that could revolutionize the field.",
        date: "November 10, 2023",
        related_laboratory: {
            title: "Quantum Computing Lab",
        },
    },
    {
        title: "New Laboratory Opening",
        body: "We are excited to announce the grand opening of our state-of-the-art nanotechnology facility.",
        date: "October 20, 2023",
        related_laboratory: {
            title: "Nanotechnology Research Center",
        },
    },
    {
        title: "Award-Winning Publication",
        body: "One of our researchers has been honored with a prestigious award for their groundbreaking work.",
        date: "September 5, 2023",
        related_laboratory: {
            title: "Material Science Institute",
        },
    },
];

const NewsCard = ({ title, body, date, related_laboratory }) => {
    return (
        <article className="flex flex-col rounded-3xl border border-black border-solid min-w-[240px] w-[325px]">
            <div className="relative">
                <img
                    loading="lazy"
                    src="/api/placeholder/400/320"
                    alt={title}
                    className="w-full rounded-tl-3xl rounded-tr-3xl aspect-[1.3]"
                />
            </div>
            <div className="flex flex-col p-6 w-full bg-cyan-200 rounded-bl-3xl rounded-br-3xl border border-black border-solid max-md:px-5">
                <div className="flex flex-col w-full text-black">
                    <h3 className="text-2xl font-bold leading-snug line-clamp-2 indent-clamp title-clamp">
                        {title}
                    </h3>
                    <p className="mt-2 text-base leading-6 line-clamp-3">
                        {body}
                    </p>
                </div>
                <div className="flex gap-4 items-center mt-6 w-full text-sm">
                    <img
                        loading="lazy"
                        src="/api/placeholder/48/48"
                        alt={`${title} avatar`}
                        className="object-contain shrink-0 self-stretch my-auto w-12 rounded-3xl aspect-square"
                    />
                    <div className="flex flex-col flex-1 shrink self-stretch my-auto basis-0 min-w-[240px]">
                        <div className="font-semibold text-black">
                            {related_laboratory.title}
                        </div>
                        <div className="gap-2 self-stretch w-full text-black">
                            {date}
                        </div>
                    </div>
                </div>
            </div>
        </article>
    );
};

const RecentNews = () => {
    return (
        <Card className="w-full max-w-4xl">
            <CardHeader>
                <CardTitle>Latest News</CardTitle>
            </CardHeader>
            <CardContent>
                <div className="flex flex-wrap gap-10 justify-between">
                    {newsItems.map((item, index) => (
                        <NewsCard key={index} {...item} />
                    ))}
                </div>
            </CardContent>
        </Card>
    );
};

const CSSTailwind = () => {
    return (
        <>
            <RecentNews rowData={newsItems} />
        </>
    );
};
export default CSSTailwind;
