import React from "react";
import EventCard from "../../Cards/Event-Card";
import { getData } from "../../../api/api-method";
import { useInfiniteFetch } from "api/custom-hooks";
const RecentEvents = ({ toFetchedData = {}, topic = "events" }) => {
    const recentEventsQuery = useInfiniteFetch({
        id: toFetchedData.id,
        url: toFetchedData.url,
        pageSize: toFetchedData.pageSize,
    });
    // console.log(recentEventsQuery);
    const { data, isLoading, isError, isPaused } = recentEventsQuery;
    return (
        <section className="flex flex-col px-8 py-16 w-full bg-gray-100 max-md:px-4 max-md:py-12">
            <div className="flex flex-col w-full text-gray-800">
                <h2 className="text-4xl font-extrabold leading-tight max-md:text-3xl">
                    Latest Events
                </h2>
                <p className="mt-4 text-base max-md:text-sm">
                    Lorem ipsum dolor sit amet, consectetur adipiscing elit.
                    Suspendisse varius enim in eros elementum tristique.
                </p>
            </div>
            <div className="flex flex-col mt-12 w-full max-md:mt-8">
                {!isLoading ? (
                    data.pages[0].map((item, index) => {
                        console.log(item.Event);
                        return <EventCard key={`${index}`} {...item.Event} />;
                    })
                ) : (
                    <div>Loading...</div>
                )}
                {!isError && (
                    <button className="px-6 py-3 mt-6 w-full text-base text-white bg-blue-600 rounded-lg hover:bg-blue-700 max-md:px-4">
                        Load More
                    </button>
                )}
            </div>
        </section>
    );
};

export default RecentEvents;
